import React, { Component } from "react";

import BlogPostContent from "./content/blog/blogContent.component";
import SearcForm from "./content/searchForm.component";

import { Add } from "../../../styled-components/admin.styled-components";

import {
  H6,
  Panel,
  Main
} from "../../../styled-components/admin.styled-components";

export default class BlogPanel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      createBlogPost: null,
      editBlogPost: null,
      others: null
    };

    this.componentDidMount = this.componentDidMount.bind(this);
  }

  componentDidMount() {
    this.setState({
      createBlogPost: true,
      editBlogPost: false,
      others: false
    });
  }

  render() {
    return (
      <Panel style={{ marginRight: "13px", marginTop: "3px" }}>
        <header style={{ paddingBottom: "10px" }}>
          <H6>BLOG</H6>
          <div className="row">
            <div className="col-10">
              <SearcForm />
            </div>
            <div className="col-2">
              <Add to="/add/blog-post">
                <i className="fas fa-plus"></i>
              </Add>
            </div>
          </div>
        </header>
        <section>
          <Main>
            <BlogPostContent />
          </Main>
        </section>
      </Panel>
    );
  }
}
